// frontend/src/App.js
import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [items, setItems] = useState([]);
  const [tip, setTip] = useState(0);
  const [people, setPeople] = useState([]);
  const [assignments, setAssignments] = useState({});

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    const formData = new FormData();
    formData.append('bill', file);

    const res = await axios.post('http://localhost:5000/upload', formData);
    setItems(res.data.items);
  };

  return (
    <div className="App">
      <h2>Bill Splitter</h2>
      <input type="file" onChange={handleFileUpload} />
      <br /><br />

      {items.length > 0 && (
        <>
          <h3>Items</h3>
          <ul>
            {items.map((item, i) => (
              <li key={i}>
                {item.name} - ₹{item.price}
              </li>
            ))}
          </ul>

          <input
            type="number"
            placeholder="Add Tip"
            value={tip}
            onChange={(e) => setTip(Number(e.target.value))}
          />
          <br /><br />

          <input
            type="text"
            placeholder="Enter comma-separated names (e.g. John,Alice)"
            onBlur={(e) => setPeople(e.target.value.split(','))}
          />
          <br /><br />

          <h4>Assign Items</h4>
          {items.map((item, i) => (
            <div key={i}>
              {item.name}: 
              <select
                onChange={(e) =>
                  setAssignments({ ...assignments, [i]: e.target.value })
                }
              >
                <option value="">-- Assign --</option>
                {people.map((p, idx) => (
                  <option key={idx} value={p}>{p}</option>
                ))}
              </select>
            </div>
          ))}

          <br /><h3>Summary</h3>
          <ul>
            {people.map((person, i) => {
              const total = items.reduce((sum, item, idx) => {
                return assignments[idx] === person
                  ? sum + item.price
                  : sum;
              }, 0);
              const personTip = (total / items.reduce((sum, i) => sum + i.price, 0)) * tip;
              return (
                <li key={i}>
                  {person}: ₹{(total + personTip).toFixed(2)}
                </li>
              );
            })}
          </ul>
        </>
      )}
    </div>
  );
}

export default App;
