import React, { useState } from 'react';
import axios from 'axios';

function UploadPage() {
  const [image, setImage] = useState(null);
  const [items, setItems] = useState([]);

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!image) return alert("Please select an image.");
    const formData = new FormData();
    formData.append('image', image);

    try {
      const res = await axios.post('http://localhost:5000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setItems(res.data.items);
    } catch (error) {
      console.error("Upload failed:", error);
      alert("Upload failed. Check backend.");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-100 to-pink-100 flex justify-center items-center p-4">
      <div className="bg-white rounded-2xl shadow-lg p-6 w-full max-w-md">
        <h1 className="text-2xl font-bold mb-4 text-center">Upload Your Bill</h1>
        <div className="border-2 border-dashed border-blue-400 rounded-xl p-6 mb-4 text-center text-gray-500">
          Drag & Drop Bill Image Here
        </div>
        <input type="file" accept="image/*" onChange={handleImageChange} className="mb-4" />
        <button onClick={handleUpload} className="w-full bg-indigo-600 text-white py-2 rounded-xl hover:bg-indigo-700">Upload & Scan</button>

        {items.length > 0 && (
          <div className="mt-6">
            <h2 className="font-semibold mb-2">Scanned Items:</h2>
            <ul className="space-y-1">
              {items.map((item, idx) => (
                <li key={idx} className="flex justify-between">
                  <span>{item.name}</span>
                  <span>â‚¹{item.price}</span>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default UploadPage;
