import React, { useState } from 'react';

const UploadPage = ({ onUpload }) => {
  const [image, setImage] = useState(null);
  const [dragOver, setDragOver] = useState(false);

  const handleChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragOver(false);
    setImage(e.dataTransfer.files[0]);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setDragOver(true);
  };

  const handleUpload = () => {
    if (!image) return;

    const formData = new FormData();
    formData.append('image', image);

    fetch('http://localhost:5000/upload', {
      method: 'POST',
      body: formData,
    })
      .then((res) => res.json())
      .then((data) => onUpload(data.items))
      .catch((err) => console.error(err));
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-gray-100 to-blue-100">
      <form
        className="w-full max-w-lg bg-white p-8 rounded-xl shadow-xl border border-gray-200"
        onSubmit={(e) => {
          e.preventDefault();
          handleUpload();
        }}
      >
        <h1 className="text-3xl font-bold mb-6 text-center text-gray-800">
          Upload Your Bill
        </h1>

        <div
          className={`border-2 border-dashed p-6 text-center mb-6 rounded-md transition-all ${
            dragOver ? 'border-blue-500 bg-blue-50' : 'border-gray-400'
          }`}
          onDrop={handleDrop}
          onDragOver={handleDragOver}
          onDragLeave={() => setDragOver(false)}
        >
          <p className="text-gray-600">Drag & Drop Bill Image Here</p>
        </div>

        <input
          type="file"
          accept="image/*"
          onChange={handleChange}
          className="mb-4 w-full border border-gray-300 rounded px-3 py-2 text-sm file:bg-blue-600 file:text-white file:border-none file:px-4 file:py-2 file:rounded file:cursor-pointer"
        />

        <button
          type="submit"
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded transition"
        >
          Upload & Scan
        </button>
      </form>
    </div>
  );
};

export default UploadPage;
