import React, { useState } from 'react';
import './UploadPage.css';
import { useNavigate } from 'react-router-dom';

const UploadPage = () => {
  const [image, setImage] = useState(null);
  const navigate = useNavigate();

  const handleUpload = async () => {
    if (!image) return alert('Select image');
    try {
      const formData = new FormData();
      formData.append('image', image);
      const resp = await fetch('http://localhost:5000/upload', { method: 'POST', body: formData });
      const data = await resp.json();
      if (!resp.ok) throw new Error(data.error || 'Upload failed');
      navigate('/items', { state: { items: data.items } });
    } catch (err) {
      alert('Failed to process the bill');
      console.error(err);
    }
  };

  return (
    <div className="upload-container">
      <form onSubmit={e => { e.preventDefault(); handleUpload(); }}>
        <h1>Upload Your Bill</h1>
        <div className="drop-zone" onDragOver={e=>e.preventDefault()} onDrop={e=>{e.preventDefault(); setImage(e.dataTransfer.files[0]);}}>
          <p>Drag & Drop Bill Image Here</p>
        </div>
        <input type="file" accept="image/*" onChange={e=>setImage(e.target.files[0])} />
        <button type="submit" className="upload-btn">Upload & Scan</button>
      </form>
    </div>
  );
};

export default UploadPage;
