// frontend/src/pages/ItemsPage.js
import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

function ItemsPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const [items, setItems] = useState(location.state?.items || []);
  const [tip, setTip] = useState(0);

  useEffect(() => {
    if (!items.length) {
      navigate('/');
    }
  }, []);

  const handleNext = () => {
    const updatedItems = items.map(item => ({ ...item, price: parseFloat(item.price) }));
    navigate('/assign', { state: { items: updatedItems, tip } });
  };

  const updateItem = (index, value) => {
    const updated = [...items];
    updated[index].name = value;
    setItems(updated);
  };

  const updatePrice = (index, value) => {
    const updated = [...items];
    updated[index].price = value;
    setItems(updated);
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-3">ðŸ§¾ Extracted Receipt Items</h2>
      <ul className="list-group">
        {items.map((item, index) => (
          <li key={index} className="list-group-item d-flex justify-content-between">
            <input
              className="form-control me-2"
              type="text"
              value={item.name}
              onChange={(e) => updateItem(index, e.target.value)}
            />
            <input
              className="form-control"
              type="number"
              placeholder="â‚¹"
              value={item.price}
              onChange={(e) => updatePrice(index, e.target.value)}
            />
          </li>
        ))}
      </ul>
      <div className="mt-4">
        <label>Add Tip (â‚¹): </label>
        <input
          type="number"
          className="form-control"
          value={tip}
          onChange={(e) => setTip(e.target.value)}
        />
      </div>
      <button className="btn btn-primary mt-4" onClick={handleNext}>
        Next: Assign People â†’
      </button>
    </div>
  );
}

export default ItemsPage;
