import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function UploadPage() {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const navigate = useNavigate();

  const handleDrop = (e) => {
    e.preventDefault();
    const selectedFile = e.dataTransfer.files[0];
    handleFile(selectedFile);
  };

  const handleFile = (selectedFile) => {
    setFile(selectedFile);
    setPreview(URL.createObjectURL(selectedFile));
  };

  const handleFileInput = (e) => {
    handleFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append("bill", file);

    try {
      const res = await axios.post("http://localhost:5000/api/upload", formData);
      const extractedItems = res.data.items; // assuming items from backend
      localStorage.setItem("items", JSON.stringify(extractedItems));
      navigate("/items");
    } catch (error) {
      alert("Upload failed.");
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Upload Your Bill</h2>

      <div
        onDrop={handleDrop}
        onDragOver={(e) => e.preventDefault()}
        style={{
          border: "2px dashed gray",
          padding: "2rem",
          marginBottom: "1rem",
          textAlign: "center",
        }}
      >
        Drag & Drop Bill Image Here
      </div>

      <input type="file" accept="image/*" onChange={handleFileInput} />

      {preview && (
        <div style={{ marginTop: "1rem" }}>
          <img src={preview} alt="Preview" height="200px" />
        </div>
      )}

      <button onClick={handleUpload} style={{ marginTop: "1rem" }}>
        Upload & Scan
      </button>
    </div>
  );
}

export default UploadPage;
