import React, { useState } from 'react';
import './UploadPage.css';
import { useNavigate } from 'react-router-dom';

const UploadPage = ({ onUpload }) => {
  const [image, setImage] = useState(null);
  const [dragOver, setDragOver] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragOver(false);
    setImage(e.dataTransfer.files[0]);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setDragOver(true);
  };

  const handleUpload = () => {
    if (!image) return;

    const formData = new FormData();
    formData.append('image', image);

    fetch('http://localhost:5000/upload', {  // ✅ Fixed endpoint (removed /api)
      method: 'POST',
      body: formData,
    })
      .then((res) => res.json())
      .then((data) => {
        console.log("Received from backend:", data);
        onUpload(data.items); // ✅ Correct if backend returns { items: [...] }
        navigate('/items', { state: { items: data.items } });
      })
      .catch((err) => console.error("Upload error:", err));
  };

  return (
    <div className="upload-container">
      <form
        className="upload-form"
        onSubmit={(e) => {
          e.preventDefault();
          handleUpload();
        }}
      >
        <h1>Upload Your Bill</h1>

        <div
          className={`drop-zone ${dragOver ? 'drag-over' : ''}`}
          onDrop={handleDrop}
          onDragOver={handleDragOver}
          onDragLeave={() => setDragOver(false)}
        >
          <p>Drag & Drop Bill Image Here</p>
        </div>

        <input
          type="file"
          accept="image/*"
          onChange={handleChange}
        />

        <button type="submit" className="upload-btn">
          Upload & Scan
        </button>
      </form>
    </div>
  );
};

export default UploadPage;
