// frontend/src/pages/AssignPage.js
import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

function AssignPage() {
  const navigate = useNavigate();
  const location = useLocation();
  const { items, tip } = location.state || {};

  const [assignedItems, setAssignedItems] = useState(
    items.map(item => ({ ...item, person: '' }))
  );

  const handleAssign = (index, value) => {
    const updated = [...assignedItems];
    updated[index].person = value;
    setAssignedItems(updated);
  };

  const handleNext = () => {
    const isValid = assignedItems.every(item => item.person.trim() !== '');
    if (!isValid) {
      alert('Please assign all items to someone.');
      return;
    }
    navigate('/summary', { state: { items: assignedItems, tip } });
  };

  return (
    <div className="container mt-5">
      <h2>ðŸ‘¤ Assign Items to People</h2>
      <ul className="list-group mt-4">
        {assignedItems.map((item, index) => (
          <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
            <div>
              <strong>{item.name}</strong> â€” â‚¹{item.price}
            </div>
            <input
              className="form-control w-25"
              type="text"
              placeholder="Person's name"
              value={item.person}
              onChange={(e) => handleAssign(index, e.target.value)}
            />
          </li>
        ))}
      </ul>
      <button className="btn btn-success mt-4" onClick={handleNext}>
        Next: Split Summary â†’
      </button>
    </div>
  );
}

export default AssignPage;
