// frontend/src/pages/UploadPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './CommonPage.css';

function UploadPage() {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0];
    if (uploadedFile) {
      setFile(uploadedFile);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile) {
      setFile(droppedFile);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault(); // Required to allow drop
  };

  const handleUpload = async () => {
    if (!file) return;
    setLoading(true);

    const formData = new FormData();
    formData.append('image', file);

    try {
      const response = await axios.post('http://localhost:5000/api/upload', formData);
      const { items, tip } = response.data;
      navigate('/assign', { state: { items, tip } });
    } catch (error) {
      alert('Failed to process the bill.');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="centered-container">
      <div
        className="page-card drop-zone"
        onDrop={handleDrop}
        onDragOver={handleDragOver}
      >
        <h2>ðŸ“¸ Upload or Drop Your Bill</h2>
        
        <input
          type="file"
          accept="image/*"
          onChange={handleFileChange}
          className="form-control mt-3"
        />

        {file && (
          <p className="mt-2"><strong>Selected:</strong> {file.name}</p>
        )}

        <button
          className="btn btn-primary w-100 mt-3"
          onClick={handleUpload}
          disabled={loading}
        >
          {loading ? 'Processing...' : 'Scan and Continue â†’'}
        </button>
      </div>
    </div>
  );
}

export default UploadPage;
