import React from 'react';
import { useLocation } from 'react-router-dom';

function SummaryPage() {
  const location = useLocation();
  const { items = [], tip = 0 } = location.state || {};

  const totals = {};
  items.forEach(({ person, price }) => {
    if (!totals[person]) totals[person] = 0;
    totals[person] += parseFloat(price);
  });

  const tipPerPerson = tip / Object.keys(totals).length;

  return (
    <div>
      <h1>Summary</h1>
      <ul>
        {Object.entries(totals).map(([person, total]) => (
          <li key={person}>
            {person}: â‚¹{(total + tipPerPerson).toFixed(2)}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default SummaryPage;
