import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

function AssignPage() {
  const navigate = useNavigate();
  const location = useLocation();
  const { items, tip = 0 } = location.state || {};

  const [assignedItems, setAssignedItems] = useState(
    items.map(item => ({ ...item, person: '' }))
  );

  const handleAssign = (index, value) => {
    const updated = [...assignedItems];
    updated[index].person = value;
    setAssignedItems(updated);
  };

  const handleNext = () => {
    const isValid = assignedItems.every(item => item.person.trim() !== '');
    if (!isValid) {
      alert('Please assign all items to someone.');
      return;
    }
    navigate('/summary', { state: { items: assignedItems, tip } });
  };

  return (
    <div>
      <h1>Assign Items</h1>
      {assignedItems.map((item, i) => (
        <div key={i}>
          {item.name} - ₹{item.price} → 
          <input
            placeholder="Enter person"
            value={item.person}
            onChange={e => handleAssign(i, e.target.value)}
          />
        </div>
      ))}
      <button onClick={handleNext}>Next</button>
    </div>
  );
}

export default AssignPage;
