import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './UploadPage.css';

export default function UploadPage() {
  const [image, setImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleUpload = async () => {
    if (!image) return alert('Select an image first');

    setLoading(true);
    const formData = new FormData();
    formData.append('bill', image); // ✅ Match backend key: 'bill'

    try {
      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });

      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        const html = await response.text();
        throw new Error('Server returned non-JSON response:\n' + html);
      }

      const data = await response.json();
      if (!response.ok) throw new Error(data.error || 'Upload failed');

      navigate('/items', { state: { items: data.items || [] } }); // ✅ Adjust depending on API response structure
    } catch (error) {
      alert('Failed: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="upload-container">
      <form
        onSubmit={(e) => {
          e.preventDefault();
          handleUpload();
        }}
        className="upload-form"
      >
        <h1>Upload Your Bill</h1>
        <div
          className="drop-zone"
          onDragOver={(e) => e.preventDefault()}
          onDrop={(e) => {
            e.preventDefault();
            setImage(e.dataTransfer.files[0]);
          }}
        >
          <p>Drag & Drop Bill Image Here</p>
        </div>
        <input type="file" accept="image/*" onChange={(e) => setImage(e.target.files[0])} />
        <button type="submit" className="upload-btn" disabled={loading}>
          {loading ? 'Scanning...' : 'Upload & Scan'}
        </button>
      </form>
    </div>
  );
}
